import React, { Component } from "react";
import PropTypes from "prop-types";
import classNames from "classnames";
import { withStyles } from "@material-ui/core/styles";
import { AddCircle, Cancel } from "@material-ui/icons";
import { Grid, Button, TextField } from "@material-ui/core";
import THSort from "../THSort";

const styles = theme => ({
  root: {
    flexGrow: 1,
    margin: "20px 40px 0 40px"
  },
  button: {
    //margin: theme.spacing.unit
    // backgroundColor: "blue"
  },
  csfullWidth: {
    width: "100%"
  },
  margin: {
    margin: 0
  },
  rightIcon: {
    marginLeft: theme.spacing.unit
  }
});
const ranges = [
  {
    value: "0-20",
    label: "Ascending"
  },
  {
    value: "21-50",
    label: "Decending"
  }
];

class THForm extends Component {
  constructor(props) {
    super(props);

    this.state = {};
  }
  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };
  render() {
    const { classes } = this.props;
    return (
      <Grid container spacing={16}>
        <form>
          <Grid item sm={12} md={6} />
          <Grid
            item
            sm={12}
            md={6}
            container
            direction="row"
            justify="flex-end"
            alignItems="center"
          >
            <Grid item xs={6} lg={3}>
              <TextField
                className={classes.csfullWidth}
                id="outlined-with-placeholder"
                label="Task note"
                placeholder="Enter text key"
                margin="normal"
                variant="outlined"
              />
            </Grid>
            <Grid item xs={6} lg={3}>
              <THSort values={ranges} />
            </Grid>

            <Grid
              item
              md={12}
              lg={6}
              container
              direction="row"
              justify="flex-end"
              alignItems="center"
            >
              <Grid item md={4} lg={6}>
                <Button
                  fullWidth
                  variant="contained"
                  color="secondary"
                  size="large"
                >
                  Summit
                  {/* This Button uses a Font Icon, see the installation instructions in the docs. */}
                  <AddCircle className={classes.rightIcon} />
                </Button>
              </Grid>
              <Grid item md={4} lg={6}>
                <Button
                  fullWidth
                  variant="contained"
                  color="default"
                  size="large"
                  onClick={this.props.onClickCancel}
                >
                  Cancel
                  {/* This Button uses a Font Icon, see the installation instructions in the docs. */}
                  <Cancel className={classes.rightIcon} />
                </Button>
              </Grid>
            </Grid>
          </Grid>
        </form>
      </Grid>
    );
  }
}

export default withStyles(styles)(THForm);
