import React, { Component } from "react";
import "./App.css";
import { withStyles } from "@material-ui/core/styles";

import THTable from "./components/THTable";
import { Grid } from "@material-ui/core";
import THHeading from "./components/THHeading";
import THControls from "./components/THControls";
import {filter,includes,lowerCase} from 'lodash';
import { items} from './components/Mocks';
const styles = theme => ({
  root: {
    flexGrow: 1,
    margin: "20px 40px 0 40px"
  }
});

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      listDataOrgin:items,
      isShowForm:false,
      strSearch:'',

    };
    this.handdleToogle = this.handdleToogle.bind(this);
  }

  handdleToogle(){
    this.setState({isShowForm:!this.state.isShowForm});
  }

  handdleChageSearch = (value) =>{
    this.setState({strSearch:value});
  }

  render() {
    const { classes } = this.props;
    let listData =[];
    const { listDataOrgin , isShowForm,strSearch } = this.state; 
    listData = filter(listDataOrgin,(item)=>{
        return includes( lowerCase(item.name) ,lowerCase(strSearch));
    })

    return (
      <div className={classes.root}>
        <Grid container spacing={24}>
          <THHeading />
           <THControls  onHandleSearch={this.handdleChageSearch}    onClickAdd={this.handdleToogle}  isShowForm={isShowForm} /> 
          <THTable listData={listData} />
        </Grid>
      </div>
    );
  }
}

// App.propTypes = {
//   classes: PropTypes.object.isRequired,
// };

export default withStyles(styles)(App);
